using namespace std;

Queue::Queue() : cstack(), tail(NULL)
{}

Queue::Queue(Node*& Ptr) : cstack(Ptr), tail(Ptr)                 
{}
Queue::Queue(const Queue& source) {
	this->top = source.top;
	this->tail = source.tail;
	if (source.top) {
		Node* sPtr;
		tail = top = new Node(*source.top);
		sPtr = source.top->next;
		while (sPtr) {
			tail->next = new Node(*sPtr);
			sPtr = sPtr->next;
			tail = tail->next;
		}

	}
}
Queue& Queue::operator=(const Queue& source)
{
	if (this == &source)  
		return *this;                

	if (true)
	{
		Queue temp;

		temp.top = top;
		temp.tail = tail;
	}

	if (true)
	{
		Queue temp = source;

		top = temp.top;
		tail = temp.tail;

		temp.top = temp.tail = NULL;
	}

	return *this;
}
